mapper.select:
select employeeid,name
from tbl_employee
where fk_dcode = #{fk_dcode} and employeeid != #{employeeid}

select rno,pNo,fk_dcode,nvl(assignDate,'-') as assignDate,nvl(startDate,'-') as startDate,nvl(dueDate,'-') as dueDate,nvl(endDate,'-') as endDate,fk_employeeid,hurryno,nvl(ingdetail,'-') as ingdetail,pName,fk_managerid,name,employeeName from ( select row_number() over(order by hurryno desc, dueDate asc) as rno, P.pNo,E.name as name,E1.name as employeeName, T.fk_dcode,to_char(assignDate,'yyyy-mm-dd') as assignDate, to_char(T.startDate ,'yyyy-mm-dd') as startDate, to_char(dueDate ,'yyyy-mm-dd') as dueDate, to_char(T.endDate ,'yyyy-mm-dd') as endDate, T.fk_employeeid,hurryno,ingdetail,pname, T.fk_managerid as fk_managerid from tbl_todo T join tbl_product P on P.pNo = T.fk_pNo join tbl_employee E on E.employeeid = T.fk_managerid join tbl_employee E1 on E1.employeeid = T.fk_employeeid where T.fk_managerid = #{employeeid} and pname like '%' || #{searchProject} || '%' and E1.name like '%' || #{searchWhoCharge} || '%' ${statusChoice}
and dueDate >= to_date(to_char(sysdate-to_number(#{period}),'yyyy/mm/dd'),'yyyy/mm/dd')
)V where rno between #{startRno} and #{endRno}

   		select count(*)
		from tbl_todo
		where enddate is not null and fk_dcode =#{string}
