mapper.select:
select count(*) from tbl_noticeboard where status=1
and fk_categnum = #{fk_categnum}
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'

select rno, seq, fk_categnum, fk_employeeid, name, subject, readCount, regDate, fileName, readStatus from ( select row_number() over(order by seq desc) as rno, seq, fk_categnum, fk_employeeid, name, subject, readCount, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate, fileName, readStatus from tbl_noticeboard where status = 1
and fk_categnum = #{fk_categnum}
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
) V where rno between #{startRno} and #{endRno}

select previousseq, previoussubject, seq, fk_categnum, fk_employeeid, name, subject, content, readcount, regDate, nextseq,nextsubject, fileName, orgFilename, fileSize, readStatus from ( select lag(seq,1) over(order by seq desc) as previousseq , lag(subject,1) over(order by seq desc) as previoussubject
, seq, fk_categnum, fk_employeeid, name, subject, content , readcount, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate
,lead(seq,1) over(order by seq desc) as nextseq ,lead(subject,1) over(order by seq desc) as nextsubject ,fileName, orgFilename, fileSize, readStatus from tbl_noticeboard where status = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
) V where V.seq = #{seq}

select seq, fk_employeeid, name, subject, readCount, regDate, privatePost, commentCount, groupno, parentSeq, depthno, fileName from ( select rownum as rno, seq, fk_employeeid, name, subject, readCount, regDate, privatePost, commentCount, groupno, parentSeq, depthno, fileName from ( select seq, fk_employeeid, name, subject, readCount, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate,privatePost, commentCount, groupno, parentSeq, depthno, fileName from tbl_suggestionboard where status = 1
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
start with parentSeq = 0 connect by prior seq = parentSeq order siblings by groupno desc, seq asc ) V ) T where rno between #{startRno} and #{endRno}

select previousseq, previoussubject, seq, fk_employeeid, name, subject, content, readcount, regDate,privatePost, nextseq,nextsubject, groupno, parentSeq, depthno, fileName, orgFilename, fileSize, commentCount from ( select lag(seq,1) over(order by seq desc) as previousseq , lag(subject,1) over(order by seq desc) as previoussubject
, seq, fk_employeeid, name, subject, content, privatePost, commentCount , readcount, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate
,lead(seq,1) over(order by seq desc) as nextseq ,lead(subject,1) over(order by seq desc) as nextsubject
,groupno, parentSeq, depthno, fileName, orgFilename, fileSize from tbl_suggestionboard where status = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
) V where V.seq = #{seq}

select seq, name, content, regDate, fk_employeeid 
from
(
	select row_number() over(order by seq desc) as rno, seq, name, content, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate
	,fk_employeeid
	from tbl_suggestionboardcomment
	where fk_seq = #{fk_seq}
) V
where rno between #{startRno} and #{endRno}

select count(*)
from tbl_suggestionboardcomment
where fk_seq=#{seq}

select ceil(count(*)/#{sizePerPage})
from tbl_suggestionboardcomment
where fk_seq = #{fk_seq}

select rno, seq, fk_employeeid, name, subject, readCount, regDate, fileName, commentCount from ( select row_number() over(order by seq desc) as rno, commentCount, seq, fk_employeeid, name, subject, readCount, fileName, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate from tbl_generalboard where status = 1
and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
) V where rno between #{startRno} and #{endRno}

select previousseq, previoussubject, seq, fk_employeeid, name, subject, content, readcount, regDate, nextseq,nextsubject , fileName, orgFilename, fileSize, commentCount from ( select lag(seq,1) over(order by seq desc) as previousseq , lag(subject,1) over(order by seq desc) as previoussubject
, seq, fk_employeeid, name, subject, content, fileName, orgFilename, fileSize, commentCount , readcount, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate
,lead(seq,1) over(order by seq desc) as nextseq ,lead(subject,1) over(order by seq desc) as nextsubject from tbl_generalboard where status = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
) V where V.seq = #{seq}

select seq, name, content, regDate, fk_employeeid 
from
(
	select row_number() over(order by seq desc) as rno, seq, name, content, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate
	,fk_employeeid
	from tbl_generalboardcomment
	where fk_seq = #{fk_seq}
) V
where rno between #{startRno} and #{endRno}

select ceil(count(*)/#{sizePerPage})
from tbl_generalboardcomment
where fk_seq = #{fk_seq}

select count(*)
from tbl_generalboardcomment
where fk_seq=#{seq}
