mapper.select:
select rsroomno,fk_roomno, fk_employeeid, name, rsubject, rdate, rtime, rdepartment
from tbl_rsroom
where fk_roomno=#{roomno} and rdate = #{rdate}

select rsgno,fk_gno, fk_employeeid, name, rgsubject, rgdate, rgtime, rgdepartment
from tbl_rsgoods
where fk_gno=#{gno} and rgdate = #{rgdate}

select rscno, fk_cno, fk_employeeid, name, rdestination, rcpeople, rcsubject, rcdate, rctime
from tbl_rscar
where fk_cno=#{cno} and rcdate = #{rcdate}
